import API from "../myAxios/API";
import queryString from 'query-string';

export function GetUserById(p_user_name, p_user_id) {

  const params = {
    p_user_name: p_user_name,
    p_user_id: p_user_id
  }

  return API({
    method: "GET",
    url: `/api/user-side/da-users/get-by-id?${queryString.stringify(params)}`,
  }).then((res) => {
    return res.data;
  }).catch((error) => {
    return error;
  });
};


export function AddUserCustomer(data) {

  //mã hóa mật khẩu
  var bodyData = { ...data }

  return API({
    method: "POST",
    url: `/api/user-side/add-user`,
    data: bodyData,
  }).then((res) => {
    return res.data;
  }).catch((error) => {
    return error;
  });
};
