// categoryApi.js
import { MD5 } from "crypto-js";
import API from "../myAxios/API";
import queryString from 'query-string';
import { User_Side_Enum } from "utils/commonData";

export function GetUserById(p_user_name, p_user_id) {

  const params = {
    p_user_name: p_user_name,
    p_user_id: p_user_id
  }

  return API({
    method: "GET",
    url: `/api/user-side/da-users/get-by-id?${queryString.stringify(params)}`,
  }).then((res) => {
    return res.data;
  }).catch((error) => {
    return error;
  });
};


export function AddUserCustomer(data) {

  //mã hóa mật khẩu
  var bodyData = { ...data, password: MD5(data.password).toString(), user_Type : User_Side_Enum.USER }

  return API({
    method: "POST",
    url: `/api/user-side/add-user`,
    data: bodyData,
  }).then((res) => {
    return res.data;
  }).catch((error) => {
    return error;
  });
};
