import API from "../myAxios/API";
import Auth from "../modules/Auth";
import { MD5 } from "crypto-js";
import { User_Side_Enum } from "utils/commonData";


export const login = async (username, password) => {
  const params = {
    p_user_name: username,
    p_password: MD5(password).toString(),
    p_type: User_Side_Enum.USER,
  };

  return await API({
    method: "GET",
    url: "/api/quan-tri/da-users/login",
    params: params
  }).then(res => {
    Auth.setCurrentUser(res.data);
    return res;
  });
};

export const register = async (username,fullname, email, password) => {
  return await API({
    method: "POST",
    url: "/users/signin",
    data: {
      username,
      fullname,
      email,
      password,
    }
  }).then(res => {
    // Auth.setUserToken(res.data.user_token);
    console.log(res);
    return res;
  });
};
